 // start of file
/**
	Panel > Attributes > Glyph
	Builds a panel of attributes for a Glyph, 
	which changes based on Shape or Path Point 
	selection.
**/


	function makePanel_GlyphAttributes(){
		// debug('\n makePanel_GlyphAttributes - START');
		var sc = getSelectedWorkItem();

		var ispointsel = false;
		if(_UI.ss && _UI.ss.objtype !== 'componentinstance') ispointsel = _UI.ss.path.sp(false);
		if(!(_UI.selectedtool === 'pathedit' || _UI.selectedtool === 'pathaddpoint')) ispointsel = false;

		var content = '<div class="navarea_header">';
		content += makePanelSuperTitle();
		content += '<h1 class="paneltitle">attributes</h1>';
		content += '</div>';
		if(!sc) return content;

		content += '<div class="panel_section">';
		content += '<table class="detail">';

		if (_UI.navhere === 'components'){
			// debug(" \t  detected navhere = components");
			content += '<tr><td colspan=2 class="detailtitle"><h3>component</h3></td></tr>';
			content += '<tr><td> name </td><td><input class="namewidth" type="text" value="'+sc.name+'" onchange="getSelectedWorkItem().name = this.value;"/></td></tr>';
		}

		if(_UI.ss && _UI.ss.objtype === 'componentinstance'){
			// component selected
			// debug(" \t component selected");
			content += componentInstanceDetails(_UI.ss);
		} else if (_UI.ss){
			// regular shape selected
			// debug(" \t regular shape selected");
			content += shapeDetails(_UI.ss);
			if(ispointsel){ content += pointDetails(_UI.ss); }
		} else {
			// no shape selected
			// debug(" \t no shape selected");
			content += glyphDetails();
		}

		if (_UI.navhere === 'components'){
			content += '<tr><td colspan=2 class="detailtitle"><h3>glyphs that use this component</h3></td></tr>';
			content += '<tr><td colspan=2>';
			if(sc.usedin.length > 0){
				content += makeUsedInThumbs();
			} else {
				content += '<br><i>this component is not currently being used by any glyphs. ';
				content += '<a href="#" onclick="showDialog_LinkComponentToGlyph();">add this component to a glyph now</a>.</i>';
			}
			content += '</td></tr>';
		}

		content += '</table><br>';
		content += '</div>';

		// debug(' makePanel_GlyphAttributes - END\n');
		return content;
	}

	function glyphDetails(s){
		var sc = getSelectedWorkItem();
		var svc = _GP.projectsettings.spinnervaluechange*1 || 1;
		sc.calcGlyphMaxes();
		var content = '';
		var numshapes = getSelectedWorkItemShapes().length;


		if(numshapes > 1){
			content += '<tr><td colspan=2><h3 style="margin-top:0px;"> bulk-transform glyph shapes </h3></td></tr>';
			content += '<tr>'+
				'<td>x'+dimSplit()+'y</td>'+
				'<td>'+
					'<input type="number" id="charx" step="'+svc+'" '+
					'onchange="_UI.focuselement=this.id; if(!_UI.redrawing){getSelectedWorkItem().setGlyphPosition(this.value, false, true); history_put(\'Glyph X Position : \'+this.value); redraw(\'Glyph Details - X Position\');}"'+
					' value="' + round(sc.maxes.xmin, 3) + '" >'+
					dimSplit()+
					'<input type="number" id="chary" step="'+svc+'" '+
					'onchange="_UI.focuselement=this.id; if(!_UI.redrawing){getSelectedWorkItem().setGlyphPosition(false, this.value, true); history_put(\'Glyph Y Position : \'+this.value); redraw(\'Glyph Details - Y Position\');}"'+
					' value="' + round(sc.maxes.ymax, 3) + '" >'+
				'</td>'+
			'</tr>';

			content += '<tr>'+
				'<td>width'+dimSplit()+'height</td>'+
				'<td>'+
					'<input type="number" id="charw" step="'+svc+'" '+
					'onchange="_UI.focuselement=this.id; if(!_UI.redrawing){getSelectedWorkItem().setGlyphSize(this.value,false,'+sc.ratiolock+'); history_put(\'Glyph Width : \'+this.value); redraw(\'Glyph Details - Width\');}"'+
					' value="' + round(sc.maxes.xmax-sc.maxes.xmin, 3) + '" >'+
					dimSplit()+
					'<input type="number" id="charh" step="'+svc+'" '+
					'onchange="_UI.focuselement=this.id; if(!_UI.redrawing){getSelectedWorkItem().setGlyphSize(false,this.value,'+sc.ratiolock+'); history_put(\'Glyph Height : \'+this.value); redraw(\'Glyph Details - Height\');}"'+
					' value="' + round(sc.maxes.ymax-sc.maxes.ymin, 3) + '" >'+
				'</td>'+
			'</tr>';

			content += '<tr>'+
				'<td> lock aspect ratio </td>'+
				'<td>'+checkUI('getSelectedWorkItem().ratiolock', sc.ratiolock, true)+'</td>'+
			'</tr>';

			content += '<tr><td colspan=2>'+
				'<table class="actionsgrid"><tr><td>'+
				'<button onclick="getSelectedWorkItem().flipNS(); history_put(\'Flip Glyph : Horizontal\'); redraw(\'Glyph Details - FlipNS\');">Flip Vertical</button>'+
				'<button onclick="getSelectedWorkItem().flipEW(); history_put(\'Flip Glyph : Vertical\'); redraw(\'Glyph Details - FlipEW\');">Flip Horizontal</button>'+
				'</td></tr></table>'+
			'</td></tr>';
		}

		if (_UI.navhere === 'components') return content;


		// AUTO GLYPH WIDTH
		content += '<tr><td colspan=2 class="detailtitle"><h3> glyph width </h3></td></tr>';

		content += '<tr>'+
				'<td> auto calculate <span class="unit">(em units)</span></td>'+
				'<td>'+
					checkUI('getSelectedWorkItem().isautowide', sc.isautowide, true)+
					'&emsp;';

					if(!sc.isautowide){
						content += '<input type="number" id="charaw" step="'+svc+'" '+
						'value="' + round(sc.glyphwidth, 3) + '" '+
						'onchange="_UI.focuselement=this.id; getSelectedWorkItem().glyphwidth = (this.value*1); redraw(\'glyphDetails\');">';
					} else {
						content += '<input type="number" disabled="disabled" '+
						'value="'+ round(sc.glyphwidth, 3) + '"/>';
					}

			content += '</td>'+
			'</tr>';



		// LEFT SIDE BEARING
		if(sc.isautowide){
			content += '<tr><td colspan=2 class="detailtitle"><h3> left side bearing </h3></td></tr>';

			content += '<tr>'+
				'<td> use default <span class="unit">(em units)</span> </td>'+
				'<td>'+
					checkUI('getSelectedWorkItem().leftsidebearing', sc.leftsidebearing, true, true)+
					'&emsp;';

					if(sc.leftsidebearing){
						if(sc.leftsidebearing === true) sc.leftsidebearing = _GP.projectsettings.defaultlsb;
						content += '<input type="number" id="charlsb" step="'+svc+'" '+
						'value="' + sc.leftsidebearing + '" '+
						'onchange="_UI.focuselement=this.id; getSelectedWorkItem().leftsidebearing = (this.value*1); redraw(\'glyphDetails\');">';
					} else {
						content +='<input type="number" disabled="disabled" '+
						'value="'+ round(_GP.projectsettings.defaultlsb, 3) + '"/>';
					}
			content += '</td>'+
			'</tr>';

		}


		// RIGHT SIDE BEARING
		if(sc.isautowide){
			content += '<tr><td colspan=2 class="detailtitle"><h3> right side bearing </h3></td></tr>';

			content += '<tr>'+
				'<td> use default <span class="unit">(em units)</span> </td>'+
				'<td>'+
					checkUI('getSelectedWorkItem().rightsidebearing', sc.rightsidebearing,  true, true)+
					'&emsp;';

					if(sc.rightsidebearing){
						if(sc.rightsidebearing === true) sc.rightsidebearing = _GP.projectsettings.defaultrsb;
						content += '<input type="number" id="charrsb" step="'+svc+'" '+
						'value="' + sc.rightsidebearing + '" '+
						'onchange="_UI.focuselement=this.id; getSelectedWorkItem().rightsidebearing = (this.value*1); redraw(\'glyphDetails\');">';
					} else {
						content +=	'<input type="number" disabled="disabled" '+
						'value="'+round(_GP.projectsettings.defaultrsb, 3) + '"/>';
					}
			content += '</td>'+
			'</tr>';

		}

		// USED IN 
		if(sc.usedin.length > 0){
			content += '<tr><td colspan=2><br class="detailtitle"><h3>glyphs that use this component</h3></td></tr>';
			content += '<tr><td colspan=2>';
			content += makeUsedInThumbs();
			content += '</td></tr>';
		} 

		return content;
	}

	function shapeDetails(s){
		//debug("SHAPEDETAILS - Drawing Shape Details");
		var svc = _GP.projectsettings.spinnervaluechange*1 || 1;
		var content = '';
		content += '<tr><td colspan=2 class="detailtitle"><h3 style="margin:0px;">shape</h3></td></tr>';

		content += '<tr>'+
			'<td> name </td>'+
			'<td>'+
				'<input class="namewidth" type="text" value="' + s.name + '" onchange="_UI.ss.changeShapeName(this.value);">'+
			'</td>'+
		'</tr>';

		var xval, yval, hval, wval;

		if(!_UI.eventhandlers.tempnewbasicshape){
			xval = round(s.path.maxes.xmin, 3);
			yval = round(s.path.maxes.ymax, 3);
			wval = round(s.path.getWidth(), 3);
			hval = round(s.path.getHeight(), 3);
		} else {
			xval = round(_UI.eventhandlers.tempnewbasicshape.xmin, 3);
			yval = round(_UI.eventhandlers.tempnewbasicshape.ymax, 3);
			wval = Math.abs(round(_UI.eventhandlers.tempnewbasicshape.xmax-_UI.eventhandlers.tempnewbasicshape.xmin, 3));
			hval = Math.abs(round(_UI.eventhandlers.tempnewbasicshape.ymax-_UI.eventhandlers.tempnewbasicshape.ymin, 3));
		}

		content += '<tr>'+
			'<td>x'+dimSplit()+'y</td>'+
			'<td>'+
				'<div class="lockwrapper">'+
					lockUI('_UI.ss.xlock', _UI.ss.xlock, 'ssxlock')+
					'<input type="number" id="shapex" step="'+svc+'" '+
					(s.xlock? 'disabled="disabled"' : 'onchange="_UI.focuselement=this.id; if(!_UI.redrawing){_UI.ss.setShapePosition(this.value, false); history_put(\'Shape X Position : \'+this.value); redraw(\'shapeDetails - X Position\');}"')+
					' value="' + xval + '" >'+
				'</div>'+
				dimSplit()+
				'<div class="lockwrapper">'+
					lockUI('_UI.ss.ylock', _UI.ss.ylock, 'ssylock')+
					'<input type="number" id="shapey" step="'+svc+'" '+
					(s.ylock? 'disabled="disabled"' : 'onchange="_UI.focuselement=this.id; if(!_UI.redrawing){_UI.ss.setShapePosition(false, this.value); history_put(\'Shape Y Position : \'+this.value); redraw(\'shapeDetails - Y Position\');}"')+
					' value="' + yval + '" >'+
				'</div>'+
			'</td>'+
		'</tr>';

		content += '<tr>'+
			'<td>width'+dimSplit()+'height &emsp;</td>'+
			'<td>'+
				'<div class="lockwrapper">'+
					lockUI('_UI.ss.wlock', _UI.ss.wlock, 'sswlock')+
					'<input type="number" id="shapew" step="'+svc+'" '+
					(s.wlock? 'disabled="disabled"' : 'onchange="_UI.focuselement=this.id; if(!_UI.redrawing){_UI.ss.setShapeSize(this.value,false,_UI.ss.ratiolock); history_put(\'Shape Width : \'+this.value); redraw(\'shapeDetails - Width\');}"')+
					' value="' + wval + '" >'+
				"</div>"+
				dimSplit()+
				'<div class="lockwrapper">'+
					lockUI('_UI.ss.hlock', _UI.ss.hlock, 'sshlock')+
					'<input type="number" id="shapeh" step="'+svc+'" '+
					(s.hlock? 'disabled="disabled"' : 'onchange="_UI.focuselement=this.id; if(!_UI.redrawing){_UI.ss.setShapeSize(false,this.value,_UI.ss.ratiolock); history_put(\'Shape Height : \'+this.value); redraw(\'shapeDetails - Height\');}"')+
					' value="' + hval + '" ></td>'+
				'</div>'+
			'</td>'+
		'</tr>';

		var overlaphelptext = "<h1>Overlap Mode</h1><div style=\\&apos;width:500px;\\&apos;>"+
			"The Path Points that make up a shape outline have either a clockwise or counter-clockwise direction. "+
			"This path direction is also known as a path&rsquo;s &ldquo;winding&rdquo;. "+
			"Shapes with the same winding will combine, opposite windings will cut-out.<br><br>"+
			"For example, to create the glyph &lsquo;o&rsquo;, draw two overlapping oval shapes. "+
			"If the outside oval has a clockwise winding, select the inside oval and change it&rsquo;s winding to counter-clockwise. "+
			"This will result in the inside oval appearing in reverse (or cutting out) in relation to the outside oval.</div><br><br>"+
			"<table style=\\&apos;width:350px;\\&apos;><tr><td style=\\&apos;text-align:center;\\&apos;><span style=\\&apos;font-size:1.2em;\\&apos;>Same Winding</span><br></td><td style=\\&apos;text-align:center;\\&apos;><span style=\\&apos;font-size:1.2em;\\&apos;>Different Winding</span><br></td></tr>"+
			"<td style=\\&apos;text-align:center;\\&apos;><br><img src=\\&apos;data:image/png;base64,"+
			""+
			"\\&apos;></td><td style=\\&apos;text-align:center;\\&apos;><br><img src=\\&apos;data:image/png;base64,"+
			""+
			"\\&apos;></td></tr></table>";

		content += '<tr>'+
			'<td> overlap mode </td>'+
			'<td>'+
				'<button style="width:180px; height:26px; padding:0px;" onclick="_UI.ss.path.reverseWinding();history_put(\'Reverse Path Direction\');redraw(\'shapeDetails - Winding\');">'+
				(s.path.winding===0?'unknown':(s.path.winding>0?'counterclockwise&ensp;&#8634':'clockwise&ensp;&#8635'))+
				'</button>'+
				'&ensp;'+helpUI(overlaphelptext)+
			'</td>'+
		'</tr>';

		if(_UI.selectedtool !== 'pathedit') {
			content += '<tr>'+
				'<td> lock aspect ratio </td>'+
				'<td>'+checkUI('_UI.ss.ratiolock', _UI.ss.ratiolock, true)+'</td>'+
			'</tr>';
		}

		//debug("<b>SHAPE DETAILS OUTPUT:<b><br><textarea rows=9 cols=3000>" + content + "</textarea>");
		return content;
	}

	function pointDetails(s){
		var tp = s.path.sp();
		var svc = _GP.projectsettings.spinnervaluechange*1 || 1;
		var content = '';

		// POINT
		content += '<tr><td colspan=2 class="detailtitle"><h3>path point</h3></td></tr>';

		content += '<tr>'+
			'<td> selected point </td>'+
			'<td><input type="number" id="pointnum" class="lockpad" value="' + s.path.sp(true) + '" onchange="_UI.focuselement=this.id; _UI.ss.path.selectPathPoint(this.value); redraw(\'pointDetails\');"></td>'+
		'</tr>';

		content += '<tr><td> point type </td><td>';
		content += makePointButton('symmetric', (tp.type==='symmetric'));
		content += makePointButton('flat', (tp.type==='flat'));
		content += makePointButton('corner', (tp.type==='corner'));
		content += '</td></tr>';

		content += '<tr>'+
			'<td>x'+dimSplit()+'y</td>'+
			'<td>'+
				'<div class="lockwrapper">'+
					lockUI('_UI.ss.path.sp().P.xlock', _UI.ss.path.sp().P.xlock, 'Pxlock')+
					'<input type="number" id="pointx" step="'+svc+'" '+
					(tp.P.xlock? 'disabled="disabled"' : 'onchange="_UI.focuselement=this.id; _UI.ss.path.sp().setPathPointPosition(\'P\', (this.value), \'null\'); history_put(\'Point X Position : \'+this.value); redraw(\'pointDetails\');"')+
					' value="' + round(tp.P.x, 3) + '" >'+
				'</div>'+
				dimSplit()+
				'<div class="lockwrapper">'+
					lockUI('_UI.ss.path.sp().P.ylock', _UI.ss.path.sp().P.ylock, 'Pylock')+
					'<input type="number" id="pointy" step="'+svc+'" '+
					(tp.P.ylock? 'disabled="disabled"' : 'onchange="_UI.focuselement=this.id; _UI.ss.path.sp().setPathPointPosition(\'P\', \'null\', (this.value)); history_put(\'Point Y Position : \'+this.value); redraw(\'pointDetails\');"')+
					' value="' + round(tp.P.y, 3) + '" >'+
				'</div>'+
			'</td>'+
		'</tr>';


		var issymmetric = tp.type === 'symmetric';

		// HANDLE 1
		content += '<tr><td colspan=2 class="detailtitle"><h3>'+(issymmetric? '<input type="checkbox" checked disabled>' : checkUI("_UI.ss.path.sp().useh1", tp.useh1, true))+' handle 1 <span class="unit">(before the point)</span></h3></td></tr>';

		if(tp.useh1){
			content += '<tr>'+
				'<td>x'+dimSplit()+'y</td>'+
				'<td>'+
					'<div class="lockwrapper">'+
						lockUI('_UI.ss.path.sp().H1.xlock', _UI.ss.path.sp().H1.xlock, 'H1xlock')+
						'<input type="number" id="handle1x" step="'+svc+'" '+
						(tp.H1.xlock? 'disabled="disabled"' : 'onchange="_UI.focuselement=this.id; _UI.ss.path.sp().setPathPointPosition(\'H1\', (this.value), \'null\'); history_put(\'H1 X Position : \'+round(this.value)); redraw(\'pointDetails\');"')+
						' value="' + round(tp.H1.x, 3) + '" >'+
					'</div>'+
					dimSplit()+
					'<div class="lockwrapper">'+
						lockUI('_UI.ss.path.sp().H1.ylock', _UI.ss.path.sp().H1.ylock, 'H1ylock')+
						'<input type="number" id="handle1y" step="'+svc+'" '+
						(tp.H1.ylock? 'disabled="disabled"' : 'onchange="_UI.focuselement=this.id; _UI.ss.path.sp().setPathPointPosition(\'H1\', \'null\', (this.value)); history_put(\'H1 Y Position : \'+round(this.value)); redraw(\'pointDetails\');"')+
						' value="' + round(tp.H1.y, 3) + '" >'+
					'</div>'+
				'</td>'+
			'</tr>';

			content += '<tr>'+
				'<td> angle <span class="unit">(degrees)</span></td>'+
				'<td><input type="number" class="lockpad" disabled="disabled" value="'+(round(tp.getHandleAngle(tp.H1)*180/Math.PI,3) || 0)+'"></td>'+
			'</tr>';
		}

		// HANDLE 2
		content += '<tr><td colspan=2 class="detailtitle"><h3>'+(issymmetric? '<input type="checkbox" checked disabled>' : checkUI('_UI.ss.path.sp().useh2', tp.useh2, true))+' handle 2 <span class="unit">(after the point)</span></h3></td></tr>';

		if(tp.useh2){
			content += '<tr>'+
				'<td>x'+dimSplit()+'y</td>'+
				'<td>'+
					'<div class="lockwrapper">'+
						lockUI('_UI.ss.path.sp().H2.xlock', _UI.ss.path.sp().H2.xlock, 'H2xlock')+
						'<input type="number" id="handle2x" step="'+svc+'" '+
						(tp.H2.xlock? 'disabled="disabled"' : 'onchange="_UI.focuselement=this.id; _UI.ss.path.sp().setPathPointPosition(\'H2\', (this.value), \'null\'); history_put(\'H2 X Position : \'+round(this.value)); redraw(\'pointDetails\');"')+
						' value="' + round(tp.H2.x, 3) + '" >'+
					'</div>'+
					dimSplit()+
					'<div class="lockwrapper">'+
						lockUI('_UI.ss.path.sp().H2.ylock', _UI.ss.path.sp().H2.ylock, 'H2ylock')+
						'<input type="number" id="handle2y" step="'+svc+'" '+
						(tp.H2.ylock? 'disabled="disabled"' : 'onchange="_UI.focuselement=this.id; _UI.ss.path.sp().setPathPointPosition(\'H2\', \'null\', (this.value)); history_put(\'H2 Y Position : \'+round(this.value)); redraw(\'pointDetails\');"')+
						' value="' + round(tp.H2.y, 3) + '" >'+
					'</div>'+
				'</td>'+
			'</tr>';

			content += '<tr>'+
				'<td> angle <span class="unit">(degrees)</span></td>'+
				'<td><input type="number" class="lockpad" disabled="disabled" value="'+(round(tp.getHandleAngle(tp.H2)*180/Math.PI,3) || 0)+'"></td>'+
			'</tr>';
		}

		return content;
	}

	function dimSplit() { return '<span style="color:'+_UI.colors.gray.l60+'; width:20px; height:24px; text-align:center; display:inline-block; font-size:1.4em; vertical-align:-2px;">&#x2044;</span>';	}

// end of file